mapper.select:
 		SELECT * FROM
 		(SELECT rownum r, f.* from
 		(SELECT * FROM KDT_SELLLIST


	 		CATEGORY = #{category}



		 		 	SELL_LOCATION = #{sell_location}



 		ORDER BY SELL_DATE DESC) f)
 		WHERE r BETWEEN #{startNo} AND #{endNo}

SELECT count(*) FROM KDT_SELLLIST


  CATEGORY =#{category}


   SELL_LOCATION =#{sell_location}


ORDER BY SELL_DATE DESC
